import React, { AsyncStorage, useEffect, useState } from 'react';
import { ActivityIndicator, StyleSheet, Text, View, ImageBackground, ScrollView } from 'react-native';

import axios from 'axios';

import { NavigationContainer, useNavigation} from '@react-navigation/native';
import { createStackNavigator, CardStyleInterpolators } from '@react-navigation/stack';

import AppLoading from 'expo-app-loading';
import { isLoaded, useFonts } from 'expo-font';

import { Header } from './component/Header';
import { Button } from './component/Button';

const ImgBg = require('./assets/bg-aot.png');
const image = { uri: "https://reactjs.org/logo-og.png" };

const Stack = createStackNavigator();
const url = 'https://aotkios.web.app/api/v1';

export default function App() {

  let [fontsLoaded] = useFonts({
    'kanit-semibold': require('./assets/fonts/Kanit-SemiBold.ttf'),
    'kanit-medium': require('./assets/fonts/Kanit-Medium.ttf'),
    'kanit-regular': require('./assets/fonts/Kanit-Regular.ttf'),
    'kanit-light': require('./assets/fonts/Kanit-Light.ttf')
  });

  if (!fontsLoaded) {
    return <AppLoading />;
  } else {
    return (
      <View style={{flex: 1}}>
        <ImageBackground source={ImgBg} style={styles.image}>
          <Header style={{height: 100}}/> 
          <NavigationContainer>
            <Stack.Navigator headerMode={false} screenOptions={{
              cardStyleInterpolator: CardStyleInterpolators.forHorizontalIOS
            }}>
              <Stack.Screen name="HomePage" component={HomeScreen} options={{gestureDirection: 'horizontal', gestureEnabled: true}} />
              <Stack.Screen name="OptionPage" component={OptionScreen} options={{gestureDirection: 'horizontal', gestureEnabled: true}} />
            </Stack.Navigator>
          </NavigationContainer>
          <Text style={styles.footer}></Text>
        </ImageBackground>
      </View>
    );
  }
}

function HomeScreen() {
  const navigation = useNavigation();
  const [dataService, setDataService] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);

  const getService = async () => {
    await axios.post(url+'/getservice', {
        "branchID": "0001"
    }, {
      auth: {
        username: "aotsmartq",
        password: "RurFebScfw6u2wYeZL6a5rL5YKKHV5eG"
      },
      headers: {
        "x-api-key": "bVYLDtJHzY4meuGatFmmqxufbPudY3H3",
        "Content-Type": "application/json"
      }
    }).then(res=>{
      const response = res.data;
      console.log(response.data);

      setIsLoaded(true);
      setDataService(response.data);
    }).catch(err=>{
      console.log(err)
    });
  }

  useEffect(() => {
    getService();
  }, []);


    return (
      <View style={styles.container}>
        {
          (!isLoaded) ? <ActivityIndicator size="large" /> :
          <View style={styles.card}>
            <Text style={styles.head}>ระบบจองคิวล่วงหน้า</Text>
            <Text style={[styles.h1, {marginTop: 20}]}>บัตรบุคคล และ ยานพาหนะ</Text>
            { dataService.filter((item_filter)=>item_filter.type === 'อนุญาตบุคคล').map((item)=><Button key={item.key} color="blue" circle={item.button_title} text={item.name} navigation={navigation} page="OptionPage" /> )}

            <Text style={[styles.h1, {marginTop: 20}]}>บัตรจอดรถ</Text>
            { dataService.filter((item_filter)=>item_filter.type === 'อนุญาตจอดรถ').map((item)=><Button key={item.key} color="blue" circle={item.button_title} text={item.name} navigation={navigation} page="OptionPage" /> )}

          </View>
        }

      </View>

    );
}

function OptionScreen() {
  const navigation = useNavigation();

  return (
    <View style={styles.container}>
      {
          (!isLoaded) ? <AppLoading /> :
          <View style={styles.card}>
            <Text style={[styles.h1]}>OPTION PAGE</Text>

            <Button color="grey" circle="B" text="Menu B" navigation={navigation} page="HomePage" /> 

          </View>
      }   
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    backgroundColor: 'rgba(52, 52, 52, 0)',
    alignItems: 'center',
    justifyContent: 'center',
  },
  image: {
    flex: 1,
    resizeMode: "contain",
    justifyContent: "center"
  },
  card: {
    flex: 2.5,
    borderTopLeftRadius: 30,
    borderTopRightRadius: 30,
    backgroundColor: '#fff',
    width: '100%',
    overflow: 'hidden',
    paddingTop: 10,
    paddingBottom: 30,
    paddingLeft: 20,
    paddingRight: 20,
  },
  h1: {
    fontSize: 20,
    color: '#002458',
    fontFamily: 'kanit-regular'
  },
  head: {
    fontSize: 28,
    color: '#002458',
    fontFamily: 'kanit-regular',
    textAlign: 'center',
  }
});
